import React from "react";
import { useState, useEffect } from "react";
import {
  useLocation,
  useNavigate,
  Navigate,
  Routes,
  Route,
} from "react-router-dom";

import axios from "axios";
import "./App.css";

import Cards from "./components/Cards/Cards.jsx";
import Nav from "./components/Nav/Nav";
import About from "./components/About/About";
import Detail from "./components/Detail/Detail";
import Form from "./components/Form/Form";
import Error404 from "./components/Error404/Error404";
import Favorites from "./components/Favorites/Favorites";
import { useDispatch } from "react-redux";
import { removeFav } from "./redux/actions";

function App() {
  const [characters, setCharacters] = useState([]);
  const location = useLocation();
  const showNav = location.pathname !== "/";

  const [access, setAccess] = useState(false);

  const dispatch = useDispatch();
  const navigate = useNavigate();


  // function login(userData) {
  //    if (userData.password === PASSWORD && userData.email === EMAIL) {
  //       setAccess(true);
  //       navigate('/home');
  //    }
  // }
  
  useEffect(() => {
    !access && navigate("/");
  }, [access]);

  const login = (userData) => {
    const { email, password } = userData;
    const URL = "http://localhost:3001/rickandmorty/login/";
    axios(URL + `?email=${email}&password=${password}`).then(({ data }) => {
      const { access } = data;
      setAccess(access);
      access && navigate("/home");
    });
  };

  function logout() {
    setAccess(false);
    navigate("/");
  }

  const addCharacter = (newCharacter) => {
    // Verificar si el personaje ya existe en el arreglo characters
    const isDuplicate = characters.some(
      (character) => character.id === newCharacter.id
    );
    if (!isDuplicate) {
      setCharacters([...characters, newCharacter]);
    }
  };

  const onSearch = (id) => {
    axios(`http://localhost:3001/rickandmorty/character/${id}`)
      .then(({ data }) => {
        if (data.name) {
          addCharacter(data);
        }
      })
      .catch((error) => {
        alert("No hay personajes de este ID", error);
      });
  };

  const onClose = (id) => {
    setCharacters(characters.filter((character) => character.id !== id));
    dispatch(removeFav(id));
  };

  const addRandomCharacter = (randomCharacter) => {
    addCharacter(randomCharacter);
  };

  return (
    <>
      {showNav && (
        <Nav
          onSearch={onSearch}
          addRandomCharacter={addRandomCharacter}
          onLogout={logout}
        />
      )}

      <Routes>
        <Route
          path="/"
          element={access ? <Navigate to="/home" /> : <Form onLogin={login} />}
        />
        <Route
          path="/home"
          element={<Cards characters={characters} onClose={onClose} />}
        />
        <Route path="/about" element={<About />} />
        <Route path="/favorites" element={<Favorites />} onClose={onClose} />
        <Route path="/detail/:id" element={<Detail />} />
        <Route path="*" element={<Error404 />} />
      </Routes>
    </>
  );
}

export default App;
